// PreSurveyPage.js
import React from 'react';
import { useNavigate } from 'react-router-dom';
import Auth from '@aws-amplify/auth';

const PreSurveyPage = () => {
  const navigate = useNavigate();

  const handleAccept = () => {
    // Navigate to the simulated AI page after consent
    navigate('/survey');
  };

  const handleDeny = async () => {
    // Log the user out (using Amplify’s Auth) and return to login
    try {
      await Auth.signOut();
      navigate('/');
    } catch (error) {
      console.error('Error signing out', error);
    }
  };

  return (
    <div style={{ padding: '2rem', textAlign: 'center', fontFamily: 'sans-serif' }}>
      <h1>Pre-Survey Consent Form</h1>
      <p>
        This is the pre consent form for the research project.
        Please review the information below before proceeding.
        [Insert dummy consent text here]
      </p>
      <div style={{ marginTop: '1rem' }}>
        <button onClick={handleAccept} style={{ marginRight: '1rem', padding: '0.5rem 1rem' }}>
          Accept
        </button>
        <button onClick={handleDeny} style={{ padding: '0.5rem 1rem' }}>
          Deny
        </button>
      </div>
    </div>
  );
};

export default PreSurveyPage;
