// App.js
import React from 'react';
import {
  BrowserRouter as Router,
  Route,
  Routes,
  Navigate
} from 'react-router-dom';
import { Amplify } from 'aws-amplify';
import '@aws-amplify/ui-react/styles.css';

// Import AWS configuration
import awsConfig from './aws-exports';

// Import Auth Context
import { AuthProvider, useAuth } from './contexts/AuthContext';

// Import components
import AdminLayout from './pages/AdminLayout';
import ResearcherLayout from './pages/ResearcherLayout';
import SurveyTakerLayout from './pages/SurveyTakerLayout';
import Dashboard from './pages/Dashboard';
import ManageResearchers from './pages/ManageResearchers';
import GroupSettings from './pages/GroupSettings';
import SurveyBuilder from './pages/SurveyBuilder';
import SimulatedAI from './pages/SimulatedAI';
import Survey from './pages/Survey';
import Logout from './pages/Logout';
import Login from './pages/Login';
import UserRoleManagement from './pages/UserRoleManagement'; // Import the new component

// Initialize Amplify with the imported configuration
Amplify.configure(awsConfig);

// Protected route component
const ProtectedRoute = ({ children, requiredRole }) => {
  const { isAuthenticated, isLoading, user } = useAuth();

  if (isLoading) {
    return <div>Loading...</div>;
  }

  if (!isAuthenticated) {
    return <Navigate to="/" replace />;
  }

  // If a specific role is required, check it
  if (requiredRole && user.role !== requiredRole) {
    // Redirect to their appropriate home based on role
    if (user.role === 'admin') {
      return <Navigate to="/admin/dashboard" replace />;
    } else if (user.role === 'researcher') {
      return <Navigate to="/researcher/dashboard" replace />;
    } else {
      return <Navigate to="/survey" replace />;
    }
  }

  return children;
};

// Main app routes
const AppRoutes = () => {
  const { user, isAuthenticated, isLoading } = useAuth();

  // Prevent any routing until auth state is determined
  if (isLoading) {
    return <div>Loading authentication state...</div>;
  }

  return (
    <Routes>
      {/* Public login route */}
      <Route path="/" element={!isAuthenticated ? <Login /> : (
        user.role === 'admin' ? <Navigate to="/admin/dashboard" replace /> :
        user.role === 'researcher' ? <Navigate to="/researcher/dashboard" replace /> :
        <Navigate to="/survey" replace />
      )} />

      {/* Admin routes */}
      <Route path="/admin" element={
        <ProtectedRoute requiredRole="admin">
          <AdminLayout />
        </ProtectedRoute>
      }>
        <Route path="dashboard" element={<Dashboard />} />
        <Route path="manage-researchers" element={<ManageResearchers />} />
        <Route path="group-settings" element={<GroupSettings />} />
        <Route path="survey-builder" element={<SurveyBuilder />} />
        {/* New route for role management */}
        <Route path="manage-roles" element={<UserRoleManagement />} />
        <Route path="logout" element={<Logout />} />
        <Route path="*" element={<Navigate to="/admin/dashboard" replace />} />
      </Route>

      {/* Researcher routes */}
      <Route path="/researcher" element={
        <ProtectedRoute requiredRole="researcher">
          <ResearcherLayout />
        </ProtectedRoute>
      }>
        <Route path="dashboard" element={<Dashboard />} />
        <Route path="group-settings" element={<GroupSettings />} />
        <Route path="survey-builder" element={<SurveyBuilder />} />
        <Route path="logout" element={<Logout />} />
        <Route path="*" element={<Navigate to="/researcher/dashboard" replace />} />
      </Route>

      {/* Survey taker routes */}
      <Route path="/survey" element={
        <ProtectedRoute requiredRole="survey-taker">
          <SurveyTakerLayout />
        </ProtectedRoute>
      }>
        {/* Default survey taker page is the simulated AI scenario */}
        <Route index element={<SimulatedAI />} />
        <Route path="survey" element={<Survey />} />
        <Route path="logout" element={<Logout />} />
        <Route path="*" element={<Navigate to="/survey" replace />} />
      </Route>

      {/* Fallback route */}
      <Route path="*" element={<Navigate to="/" replace />} />
    </Routes>
  );
};

// Root app component with authentication guard
function App() {
  return (
    <AuthProvider>
      <Router>
        <AppRoutes />
      </Router>
    </AuthProvider>
  );
}

export default App;
